    private <T> void writeListToFile(List<T> listData, Map<String, String> afeMap, String etlDate){
        if(!CollectionUtils.isEmpty(listData)){
            StringBuilder stringBuilder = new StringBuilder();

            String className="";
            for(Object o :listData){
                //赋值 5个值 todo


                className = o.getClass().getName();
                stringBuilder.append(o.toString()).append("|+|\n");

                try {
                    Field[] field = o.getClass().getDeclaredFields();
                    for (int i = 0; i < field.length; i++) {
                        //字段属性为list
                        String name = field[i].getName();
                        name = name.replaceFirst(name.substring(0, 1), name.substring(0, 1).toUpperCase());// 属性首字母大写
                        Method m = o.getClass().getMethod("get" + name);
                        String type = field[i].getGenericType().toString(); // 获取属性的类型
                        //		       	if(field[i].getType().equals(List.class)){
                        //                    if(type.equals("class java.util.List")){
                        if(type.contains("java.util.List")){
                            //todo 可能里面还包含list
                            List list = (List)m.invoke(o);
                            writeListToFile(list,afeMap,etlDate);
                        }else if(!type.equals("class java.lang.String")){
                            Object object = m.invoke(o);
                            writeObjectToFile(object,afeMap,etlDate);
                        }
                    }
                } catch (Exception e) {
                    // TODO Auto-generated catch block
                    e.printStackTrace();
                }
            }
            //		String data = stringBuilder.toString().substring(0,stringBuilder.toString().length()-2);
            String data = stringBuilder.toString();
            String tableName = className.split("\\.")[ className.split("\\.").length-1].toLowerCase();
            //String etlDate = getFormatDate(getDiffDate(new Date(), 0));
            //文件名 "i_edmp_ssd1_report_20190426.dat"
            String fileName = "i_edmp_"+tableName+"_"+etlDate+".dat";
            // 路径"/edmp/receive/{$predate}/bdap/ssd1"
            String path = "D:/edmp/receive/"+etlDate+"/bdap/ssd1";
            writeFile(data,fileName,path);
        }

    }
