    private void writeFile(String data,String fileName,String path){
        BufferedWriter bufferedWriter = null;
        File f = new File(path);
        if (!f.exists()) {
            f.mkdirs();
        }
        try {
            File file = new File(path+"/"+fileName);
            if (!file.exists())
                file.createNewFile();
            //            bufferedWriter = new BufferedWriter(new OutputStreamWriter(new FileOutputStream(file, true),
            //                    HttpClientGlobalConfig.appConfig.getSecretKeyCharsetName()));
            bufferedWriter = new BufferedWriter(new OutputStreamWriter(new FileOutputStream(file, true),
                    "UTF-8"));
            bufferedWriter.write(data);
        } catch (Exception e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
        }finally {
            try {
                if (bufferedWriter != null) {
                    bufferedWriter.close();
                }
            } catch (IOException e) {
                //                LOG.info(HttpClientConstants.LOG_PREFIX + "  writeFile,  IOException", e);
                e.printStackTrace();
            }

        }
    }

